const rawContent = $json.choices?.[0]?.message?.content;

if (!rawContent) {
  const originalQuestion = $node["Format_Filtered_Data"].json.question || "Question not found";

  return [{
    json: {
      answer: null,
      confidence_score: 0,
      sources: [],
      question: originalQuestion,
      parsing_error: true
    }
  }];
}

const originalQuestion = $node["Format_Filtered_Data"].json.question 
  || $node["Filter_Relevant_Articles"].json.question 
  || $node["Webhook_Customer_Input"].json.body?.question 
  || "Question not found";

let parsed = {};
try {
  parsed = JSON.parse(rawContent);

  if (parsed.answer === null || parsed.answer === undefined) {
    parsed.confidence_score = 0;
    parsed.sources = [];
  }

} catch (e) {
  console.log("JSON parsing failed:", e.message);
  parsed = {
    answer: null,
    confidence_score: 0,
    sources: [],
    parsing_error: true
  };
}

parsed.question = originalQuestion;

return [{
  json: parsed
}];

// Get the customer's question - now from our error handler
const customerQuestion = ($json.question || "").toLowerCase();

// Get KB rows - handle both normal and fallback modes
const kbRows = $json.relevantEntries || [];

// If we're in fallback mode, return empty (triggers escalation)
if ($json.fallback_mode === true || kbRows.length === 0) {
  return [{
    json: {
      question: customerQuestion,
      relevantEntries: [],
      fallback_triggered: true
    }
  }];
}

// Normal filtering logic (unchanged)
const relevantEntries = kbRows
  .filter(entry => {
    const title = (entry.title || "").toLowerCase();
    const content = (entry.content || "").toLowerCase();

    return (
      (customerQuestion.includes("password") && (title.includes("password") || content.includes("password"))) ||
      (customerQuestion.includes("billing") && (title.includes("billing") || content.includes("billing"))) ||
      (customerQuestion.includes("subscription") && (title.includes("subscription") || content.includes("subscription"))) ||
      (customerQuestion.includes("refund") && (title.includes("refund") || content.includes("refund"))) ||
      (customerQuestion.includes("authentication") && (title.includes("authentication") || content.includes("authentication")))
    );
  });

// Return results
const finalEntries = relevantEntries.length > 0 ? relevantEntries : kbRows;

return [{
  json: {
    question: customerQuestion,
    relevantEntries: finalEntries,
    fallback_triggered: false
  }
}];

// Get the question and relevant entries from the previous node
const question = $json.question || "";
const relevantEntries = $json.relevantEntries || [];

// Build the knowledge base content string
const kbContent = relevantEntries.length > 0
  ? relevantEntries
      .map(entry => `ID: ${entry.id}, Title: ${entry.title}, Content: ${entry.content}`)
      .join("\n")
  : "No relevant entries found.";

// Create the request body for the LLM
const requestBody = {
  model: "gpt-3.5-turbo",
  messages: [
    {
      role: "system",
      content: `You are a helpful customer support assistant.
You MUST ONLY answer using the provided knowledge base entries.
If the customer question is not answered in the knowledge base, respond strictly with:
{
  "answer": null,
  "confidence_score": 0,
  "sources": []
}

If you can answer from the knowledge base, respond with:
{
  "answer": "[your detailed answer]",
  "confidence_score": [0.1 to 1.0 based on how well the KB answers the question],
  "sources": ["ID: [entry_id], Title: [entry_title]"]
}

Do NOT make up answers. ALWAYS respond with valid JSON.`
    },
    {
      role: "user",
      content: `Knowledge Base Entries:
${kbContent}

Customer Question: ${question}`
    }
  ],
  temperature: 0
};

// Return the properly structured output
return [{
  json: {
    requestBody: requestBody
  }
}];

