// Get the original question
const question = $json.question || "Question not found";

// Check if we have a valid answer or if this is an escalation case
const hasValidAnswer = $json.answer !== null && $json.answer !== undefined && $json.confidence_score > 0.5;

if (hasValidAnswer) {
  // Return the normal response
  return [{
    json: {
      answer: $json.answer,
      confidence_score: $json.confidence_score,
      sources: $json.sources || [],
      question: question
    }
  }];
} else {
  // Return the "no relevant sources" message
  return [{
    json: {
      answer: "I don't find any relevant sources to your question. Our support team has been notified.",
      confidence_score: 0,
      sources: [],
      question: question
    }
  }];
}

// Return the "no relevant sources" message for escalated cases
return [{
  json: {
    answer: "I don't find any relevant sources to your question. Our support team has been notified.",
    confidence_score: 0,
    sources: [],
    question: $json.question || "Question not available"
  }
}];

// Check if the previous node failed
const sheetsData = $input.all();

// If no data or error occurred
if (!sheetsData || sheetsData.length === 0 || sheetsData[0].error) {
  console.log("Google Sheets API failed - using fallback mode");

  return [{
    json: {
      question: $node["Webhook_Customer_Input"].json.body.question || "",
      relevantEntries: [], // Empty array forces escalation path
      error_source: "SHEETS_API_FAILURE",
      fallback_mode: true
    }
  }];
}

// If successful, pass data through unchanged
return [{
  json: {
    question: $node["Webhook_Customer_Input"].json.body.question || "",
    relevantEntries: sheetsData.map(item => item.json),
    error_source: null,
    fallback_mode: false
  }
}];

const inputData = $input.all();

// Check if OpenRouter request failed
if (!inputData || inputData.length === 0 || inputData[0].error) {
  console.log("OpenRouter API failed - generating fallback response");

  // Get original question for fallback response
  const originalQuestion = $node["Format_Filtered_Data"].json.question || "Question not available";

  // Create fallback AI response structure that matches expected format
  return [{
    json: {
      choices: [{
        message: {
          content: JSON.stringify({
            answer: "I'm experiencing technical difficulties connecting to our AI service. A human support representative will assist you shortly.",
            confidence_score: 0,
            sources: []
          })
        }
      }],
      error_type: "OPENROUTER_FAILURE",
      fallback_response: true
    }
  }];
}

// If successful, pass through unchanged
return inputData;

// Log Slack failures (in production, this would go to your logging system)
const inputData = $input.all();

if (!inputData || inputData.length === 0 || inputData[0].error) {
  console.error("CRITICAL: Slack notification failed!", {
    timestamp: new Date().toISOString(),
    question: $json.question,
    confidence: $json.confidence_score,
    error: "SLACK_API_FAILURE"
  });

  // Still proceed with customer response
  return [{
    json: {
      answer: "I don't find any relevant sources to your question. Our support team has been notified.",
      confidence_score: 0,
      sources: [],
      question: $json.question || "Question not available",
      escalation_logged: false,
      slack_failed: true
    }
  }];
}

// Slack succeeded, proceed normally
return [{
  json: {
    answer: "I don't find any relevant sources to your question. Our support team has been notified.",
    confidence_score: 0,
    sources: [],
    question: $json.question || "Question not available",
    escalation_logged: true,
    slack_failed: false
  }
}];
